class Solution {
public:
    int f[2020][1010];

    const int mod=1e9+7;

    int add(int x, int y){
        return (x+y>=mod? x+y-mod: x+y<0? x+y+mod: x+y);
    }

    int countOfPairs(vector<int>& w) {
        int n=w.size();
        for(int i=0; i<=w[0]; i++) f[0][i]=1;

        for(int i=1; i<n; i++){
            for(int k=0; k<=w[i-1]; k++){
                int x=max(k, k+w[i]-w[i-1]);
                int val=f[i-1][k];
                // for(int j=x; j<=w[i]; j++) f[i][j]=add(f[i][j], f[i-1][k]);
                f[i][x]=add(f[i][x], val);
                f[i][w[i]+1]=add(f[i][w[i]+1], -val);
            }
            for(int j=1; j<=w[i]; j++) f[i][j]=add(f[i][j], f[i][j-1]);
        }

        int res=0;
        for(int i=0; i<=w[n-1]; i++) res=add(res, f[n-1][i]);
        return res;
    }
};
