class Solution {
    public int countOfPairs(int[] arr) {
        int MOD = 1_000_000_007;

        int max = 0;
        for (int v : arr) {
            max = Math.max(max, v);
        }
        int N = arr.length;
        int[][] dp = new int[N][max + 1];
        int[][] sum = new int[N][max + 1];

        for (int idx = 0; idx < N; idx++) {
            for (int i = 0; i <= max; i++) {
                if (i <= arr[idx]) {
                    if (idx == 0) {
                        dp[idx][i] = arr[idx] - i >= 0 ? 1 : 0;
                    } else {
                        int minPrevI = Math.min(i, arr[idx - 1] - arr[idx] + i);
                        if (minPrevI >= 0) {
                            dp[idx][i] = sum[idx - 1][minPrevI];
                        }
                    }
                }
                if (i == 0) {
                    sum[idx][i] = dp[idx][i];
                } else {
                    sum[idx][i] = (dp[idx][i] + sum[idx][i - 1]) % MOD;
                }
//                System.out.printf(">>> idx=%d i=%d dp=%d sum=%d\n",idx,i, dp[idx][i],sum[idx][i]);
            }
        }
        return sum[N - 1][max];


    }
}
