class Solution {
public:
    const int mod = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector dp(n + 1, vector<int>(1001));
        for (int i = 0; i <= nums[0]; ++i)
            dp[1][i] = 1;

        vector<int> suf(1001);
        for (int i = nums[0]; i >= 0; --i) {
            suf[i] = 1;
            if (i < 1000) suf[i] = (suf[i] + suf[i + 1]) % mod;
        }

        for (int i = 2; i <= n; ++i) {
            for (int j = 0; j <= nums[i - 1]; ++j) {
                int k = max(j, j + nums[i - 2] - nums[i - 1]);
                if (k <= 1000)
                    dp[i][j] = suf[k];
            }

            fill(suf.begin(), suf.end(), 0);
            for (int j = nums[i - 1]; j >= 0; --j) {
                suf[j] = dp[i][j];
                if (j < 1000) suf[j] = (suf[j] + suf[j + 1]) % mod;
            }
        }

        int ans = 0;
        for (int i = 0; i <= nums.back(); ++i)
            ans = (ans + dp[n][i]) % mod;

        return ans;
    }
};
