using namespace std;
using LL = long long;
const int INF = 0x3f3f3f3f;
const LL LINF = 0x3f3f3f3f3f3f3f3f;

class Solution
{
public:
    const int MAXN = 1001;
    const int MOD = 1e9 + 7;

    int countOfPairs(vector<int> &nums)
    {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(MAXN, 0));
        for (int i = nums[n - 1]; i >= 0; i--)
            dp[n - 1][i] = nums[n - 1] - i + 1;
        for (int i = n - 2; i >= 0; i--)
        {
            for (int x = 0; x < MAXN; x++)
            {
                int minx = max(x, nums[i + 1] - nums[i] + x);
                if (minx < MAXN)
                    dp[i][x] = dp[i + 1][minx];
            }
            for (int x = MAXN - 2; x >= 0; x--)
                dp[i][x] = ((LL)dp[i][x] + (LL)dp[i][x + 1]) % MOD;
        }
        return dp[0][0];
    }
};
