typedef long long int ll;
ll MOD = 1000000007;
int cap = 1001;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int N = nums.size();
        vector<vector<ll>> DP(N,vector<ll>(cap));
        for (int t = 0; t <= nums[0]; t++) {
            ++DP[0][t];
        }
        for (int i = 1; i < N; i++) {
            vector<ll> cumsum;
            ll cum = 0;
            for (int s = 0; s < cap; s++) {
                cumsum.push_back(cum);
                cum = (cum + DP[i-1][s]) % MOD;
            }
            cumsum.push_back(cum);
            for (int t = 0; t <= nums[i]; t++) {
                int cut = max(0, min(t+1, t - nums[i] + nums[i-1] + 1));
                // cout << i << " " << " " << t << " cumsum " << cut << endl;
                DP[i][t] = (DP[i][t] + cumsum[cut]) % MOD;
            }
        }
        ll ret = 0;
        for (int t = 0; t < cap; t++) {
            ret = (ret + DP[N-1][t]) % MOD;
        }
        return ret;
    }
};
