class Solution {
public:
    int countOfPairs(vector<int>& nums) {
         int n = nums.size();
         int N = 0;
        for(auto it:nums)N = max(N , it);
        N++;
        nums.push_back(0);
        using ll = long long;
        vector<ll> dp(N , 0) , suf(N , 0);
        const int mod = 1e9 + 7;
        for(int a = 0 ; a <= nums[n-1] ; a++){
            dp[a] = 1;
        }
        suf[N-1] = dp[N-1];
        for(int i = N-2;i>=0;i--)suf[i] = dp[i] ,suf[i] += suf[i+1] , suf[i] %= mod;
        for(int i = n-2;i>=0;i--){
            vector<ll> ndp(N , 0);
            for(int a = 0 ; a <= nums[i] ; a++){
                 ll bh = nums[i] - a;
                 ll bn = bh;
                 ll an = nums[i+1] - bn;
                 ll ind = max<ll>(a , an);

                 if(ind < N)ndp[a] = suf[ind];
            }
            for(int j = N-1;j>=0;j--){
                 suf[j] = ndp[j];
                 if(j+1 < N)suf[j] += suf[j+1] , suf[j] %= mod;
            }
        dp = ndp;
        }
        ll ans = 0;
        return suf[0];

    }
};
