#define FOR(i, a, b) for (int i = (a); i < (b); i++)
#define REP(i, n) for (int i = 0; i < (n); i++)

const int M = 1001, P = 1000000007;

class Solution {
public:
  int countOfPairs(vector<int>& a) {
    int n = a.size();
    vector<vector<int>> s(n, vector<int>(M));
    REP(j, a[0]+1)
      s[0][j] = 1;
    REP(i, n-1) {
      REP(j, a[i]+1) {
        if (!s[i][j])
          continue;
        int lo = max(j, a[i+1]-a[i]+j);
        int hi = a[i+1]+1;
        if (lo < hi) {
          (s[i+1][lo] += s[i][j]) %= P;
          if (hi < M) (s[i+1][hi] -= s[i][j]) %= P;
        }
      }
      FOR(j, 1, M)
        (s[i+1][j] += s[i+1][j-1]) %= P;
    }
    int ret = 0;
    REP(j, M)
      (ret += s[n-1][j]) %= P;
    return (ret%P+P)%P;
  }
};
