class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[][] dp = new int[n + 1][1001];
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= 1000; j++) {
                dp[i][j] = 0;
            }
        }
        dp[0][0] = 1;
        int mod = (int) 1e9 + 7;
        for (int i = 1; i <= n; i++) {
            for (int k = 0; k <= (i == 1 ? 1000 : nums[i - 2]); k++) {
                if (dp[i - 1][k] == 0) continue;
                if (i == 1) {
                    dp[i][0] += dp[i - 1][k];
                    dp[i][0] %= mod;
                }
                else {
                    if (Math.max(k, nums[i - 1] - nums[i - 2] + k) > nums[i - 1]) continue;
                    dp[i][Math.max(k, nums[i - 1] - nums[i - 2] + k)] += dp[i - 1][k];
                    dp[i][Math.max(k, nums[i - 1] - nums[i - 2] + k)] %= mod;
                }
            }
            for (int j = 1; j <= nums[i - 1]; j++) {
                dp[i][j] += dp[i][j - 1];
                dp[i][j] %= mod;
            }
        }
        int ans = 0;
        for (int i = 0; i <= nums[n - 1]; i++) {
            ans = (ans + dp[n][i]) % mod;
        }
        return ans;
    }
}
