template<typename U, typename V> void Min(U &a, const V & b){if(a > b)a = b;}
template<typename U, typename V> void Max(U &a, const V & b){if(a < b)a = b;}
template<typename U, typename V> void add(U &a, const V & b){a = (a + b) % mod;}
template<typename U> U gcd(U a, U b){
    if(a == 0)return b;
    if(b == 0)return a;
    if(a >= b)return gcd(a % b, b);
    else return gcd(a, b % a);
}
int pow(int a, int b){
    int ans = 1;
    while(b){
        if(b & 1)ans = 1LL * ans * a % mod;
        a = 1LL * a * a % mod;
        b >>= 1;
    }
    return ans;
}
int pow(int a, ll b, int c){
    int ans = 1;
    while(b){
        if(b & 1)ans = 1LL * ans * a % c;
        a = 1LL * a * a % c;
        b >>= 1;
    }
    return ans;
}

int ex_gcd(int a, int b, ll &x, ll &y) {
    if (b == 0) {
        x = 1;
        y = 0;
        return a;
    }
    ll x1, y1;
    int d = ex_gcd(b, a % b, x1, y1);
    x = y1;
    y = x1 - a / b * x;
    return d;
}

struct DSU{
    vi f, sz;
    int n;
    DSU(int _n) {
        n = _n;
        f.resize(n);
        sz.resize(n);
        rep(i, 0, n) f[i] = i, sz[i] = 1;
    }
    int find(int a) {
        return f[a] == a ? a : f[a] = find(f[a]);
    }
    int merge(int a, int b) {
        a = find(a), b = find(b);
        if (a == b) return 0;
        if (sz[a] < sz[b]) swap(a, b);
        f[b] = a;
        sz[a] += sz[b];
        return 1;
    }
};

int dp[1005], c[1005];
class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = SZ(a);
        memset(dp, 0, sizeof(dp));
        rep(i, 0, a[0]+1) dp[i] = 1;
        rep(i, 0, n-1) {
            rep(j, 0, a[i+1]+1) c[j] = 0;
            rep(j, 0, a[i]+1) {
                int k = a[i] - j;
                int l = j, r = a[i+1];
                Max(l, a[i+1] - k);
                if (l <= r) {
                    add(c[l], dp[j]);
                    add(c[r+1], mod - dp[j]);
                }
            }
            rep(j, 0, a[i+1]+1) {
                dp[j] = c[j];
                if (j < a[i+1]) add(c[j+1], c[j]);
            }
        }
        int ans = 0;
        rep(i, 0, a[n-1]+1) add(ans, dp[i]);
        return ans;
    }
};
