class Solution(object):
    def countOfPairs(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        MOD = 10**9+7
        n = len(nums)
        for i in range(n-1):
            if(nums[i] < nums[i+1]):
                diff = nums[i+1]-nums[i]
                for j in range(i+1,n):
                    nums[j] -= diff
        for i in range(n-2,-1,-1):
            nums[i] = min(nums[i], nums[i+1])
        for i in range(n):
            nums[i] += 1
        k = nums[-1]
        dp = [[0 for _ in range(k+1)] for __ in range(n)]
        for i in range(1,nums[0] + 1):
            dp[0][i] = 1
        for L in range(1, n):
            for i in range(1, nums[L] + 1):
                dp[L][i] = (dp[L][i-1] + dp[L-1][i])%MOD
        ans = 0
        for i in range(k+1):
            ans = (ans + dp[-1][i])%MOD
        return(ans)
