class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int mod=1e9+7;
        vector<vector<int>> dp(n, vector<int>(1001, 0));
        vector<vector<int>> pre(n, vector<int>(1001, 0));
        for (int j=0; j<=nums[0]; j++){
            dp[0][j]=1;
            if (j-1>=0)pre[0][j]=pre[0][j-1];
            pre[0][j]++;
        }
        for (int i=1; i<n; i++){
            int start=0;
            if (nums[i-1]<nums[i])start=nums[i]-nums[i-1];
            for (int j=start; j<=nums[i]; j++){
                dp[i][j]+=pre[i-1][j-start];
                dp[i][j]%=mod;
                if (j-1>=0)pre[i][j]=pre[i][j-1];
                pre[i][j]+=dp[i][j];
                pre[i][j]%=mod;
            }
        }
        int ans=0;
        for (int i=0; i<=nums[n-1]; i++){
            ans+=dp[n-1][i];
            ans%=mod;
        }
        return ans;
    }
};
