const int mod = 1e9+7;

class Solution {
public:
    int countOfPairs(vector<int>& arr) {
        int n = arr.size();
        int m = 0;
    for(int i=0;i<n;i++){
        m = max(arr[i]+1, m);
    }

    vector<vector<int>> dp(n, vector<int>(m));
    for(int i=0;i<=arr[0];i++){
        dp[0][i] = 1;
    }
    for(int i=1;i<m;i++){
        dp[0][i]= (dp[0][i] + dp[0][i-1])%mod;
    }
    for(int i=1;i<n;i++){
        for(int j=0;j<=arr[i];j++){
            if(arr[i]<=arr[i-1]){
                dp[i][j]= (dp[i][j] + dp[i-1][j])%mod;
            } else {
                if(j-(arr[i]-arr[i-1])>=0) {
                    dp[i][j]= (dp[i][j] + dp[i-1][j-arr[i]+arr[i-1]])%mod;
                }
            }
        }
        for(int j=1;j<m;j++){
            dp[i][j] = (dp[i][j]+dp[i][j-1])%mod;
        }
    }
    return dp[n-1][m-1];
    }
};
