class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        M = 10 ** 9 + 7        
        n = len(nums)
        dp = [1] * (nums[-1] + 1)
        for i in range(n - 2, -1, -1):
            tmp = [0] * (nums[i] + 1)
            j = 0
            cnt = sum(dp) % M
            for k in range(nums[i] + 1):
                while j < len(dp) and (j < k or nums[i + 1] - j > nums[i] - k):
                    cnt -= dp[j]
                    j += 1
                tmp[k] = cnt % M
            dp = tmp
        return sum(dp) % M
