M = 1_000_000_007
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        presum = [x+1 for x in range(nums[0]+1)]
        for i in range(1, len(nums)):
            new_ = [0]*(nums[i]+1)
            for x1 in range(nums[i]+1):
                k = min(x1,nums[i-1]-(nums[i]-x1))
                v = 0
                if k >= 0:
                    v = presum[k]
                new_[x1] = v
                if x1 > 0:
                    new_[x1] = (v + new_[x1-1]) % M
            presum = new_
        return presum[-1]
