class Solution {
public:
    const int mod=1e9+7;
    int sum[1001];
    int dp[2001][1001],k,ans;
    int n;
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        for(int i=1; i<=n; i++){
            for(int j=0; j<=nums[i-1]; j++){
                if(i==1) dp[i][j]=1;
                else{
                    k=nums[i-1]-j;
                    // j<=z<=1000
                    // y<=k<=1000
                    // x-z<=y-j<=1000
                   // for(int z=0; z<=nums[i-2]; z++){
                    //    int y=nums[i-2]-z;
                    //    if(k<=nums[i-2]-z && z<=j){
                    //        dp[i][j]=(dp[i][j]+dp[i-1][z])%mod;
                    //    }
                    //}
                    // nums[i-2]-x>=k ->nums[i-2]-k>=x
                    // 0<=x<=j 
                    dp[i][j]=( min(nums[i-2]-k,j)<0?0:sum[min(nums[i-2]-k,j)]);
                }
            }
            sum[0]=dp[i][0];
            for(int j=1; j<=1000; j++) sum[j]=(dp[i][j]+sum[j-1])%mod;
        }
        for(int i=0; i<=1000; i++) ans=(ans+dp[n][i])%mod;
        return ans;
    }
};
