#define MAX_NUMS 1002
#define MODD 1000000007
#define ll long long

class Solution {
public:

    ll dp[2][MAX_NUMS+2];
    ll cdp[MAX_NUMS+2];

    int Solve(const vector<int>& nums) {
        int n=nums.size();
        for(int pos=n;pos>=1;pos--) {
            for(int prev_a=0;prev_a<=MAX_NUMS;prev_a++) {
                int prev_b = nums[pos-1]-prev_a;
                ll& ans = dp[pos&1][prev_a];
                ans=0;
                if (pos==n) {
                    ans=1;
                    continue;
                }
                int mina = max(0,max(prev_a, nums[pos]-prev_b));
                if (mina > nums[pos]) {
                    continue;
                }

                ans = cdp[nums[pos]]-(mina?cdp[mina-1]:0);
                if (ans < 0) ans+=MODD;
            }

            for(int prev_a=0;prev_a<=MAX_NUMS;prev_a++) {
                cdp[prev_a]=dp[pos&1][prev_a];
                if (prev_a) cdp[prev_a] += cdp[prev_a-1];
                cdp[prev_a]%=MODD;
            }

        }
        ll ans=0;
        for(int a=0;a<=nums[0];a++) {
            ans += dp[1][a];
            ans %= MODD;
        }
        return ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        return Solve(nums);

    }
};
