#define ll long long
ll dp[2000][1001];
ll preDp[2000][1001];
const ll M=1e9+7;
ll sum(int l,int r,int i){
    if(r<l) return 0;
    if(l==0) return preDp[i-1][r];
    return (preDp[i-1][r]-preDp[i-1][l-1]+M)%M;
}
class Solution {
public:
    int countOfPairs(vector<int>& a) {
        memset(dp,0,sizeof(dp));
        memset(preDp,0,sizeof(preDp));
        for(int i=0;i<=a[0];i++) dp[0][i]=1;
        for(int i=0;i<=a[0];i++) preDp[0][i]=(i+1);
        int n=a.size();
        for(int i=1;i<n;i++){
            for(int j=0;j<=a[i];j++){
                int r=min(j,a[i-1]-a[i]+j);int l=0;
                r=min(r,a[i-1]);
                // cout<<i<<" "<<j<<" "<<l<<" "<<r<<'\n';
                dp[i][j]=sum(l,r,i);
            }

            ll sum=0;
            for(int j=0;j<=1000;j++){
                sum=(sum+dp[i][j])%M;
                preDp[i][j]=sum;
            }
        }

        ll res=0;
        for(int j=0;j<=1000;j++) res=(res+dp[n-1][j])%M;

        return res;
    }
};
