class Solution {
public:
    bool Check(string s) {
        int r = 0;
        for (int i=0; i<s.size(); ++i) {
            r = r*10 + s[i] - '0';
            int j = 0;
            while (7*(j+1) <= r) 
                ++j;
            r -= 7*j;     
        }

        return r == 0;
    }

    string largestPalindrome(int n, int k) {
        string ans (n, '9');
        if (k==2) 
            ans[0] = ans[n-1] ='8';
        else if (k==4) {  
            ans[0] = ans[n-1] ='8';
            if (n > 2)
                ans[1] = ans[n-2] ='8';
        }
        else if (k==5) {
            ans[0] = ans[n-1] ='5';
        }
        else if (k==6) {
            if (n==1)
                return "6";
            if (n==2)
                return "66";
            if (n==3) 
                return "888";
            ans[0] = ans[n-1] ='8';
            if (n % 2 == 0) 
                ans[n/2] = ans[n/2-1]= '7';
            else 
                ans[n/2] = '8';
        }
        else if (k==7) {
            if (n < 3) {
                fill(ans.begin(), ans.end(), '7');
                return ans;
            }
            for (int i=9; i>=0; --i) {
                if (n%2 == 0) 
                    ans[n/2] = ans[n/2-1]= i + '0';
                else
                    ans[n/2] = i + '0';
                if (Check(ans))
                    return ans;
            }
        }
        else if (k==8) {
            if (n < 6) {
                fill(ans.begin(), ans.end(), '8');
                return ans;
            }
            ans[0] = ans[n-1] = '8';
            ans[1] = ans[n-2] = '8';
            ans[2] = ans[n-3] = '8';
        }
        return ans;
    }
};
