class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k == 1){
            return string(n, '9');
        }else if(k == 2){
            if(n == 1) return "8";
            return "8" + string(n-2, '9') + "8";
        }else if(k == 3){
            return string(n, '9');
        }else if(k == 4){
            if(n == 1) return "8";
            if(n == 2) return "88";
            if(n == 3) return "888";
            return "88" + string(n-4, '9') + "88";
        }else if(k == 5){
            if(n == 1) return "5";
            return "5" + string(n-2, '9') + "5";
        }else if(k == 6){
            if(n == 1) return "6";
            if(n == 2) return "66";
            if(n == 3) return "888";
            if(n % 2 == 0) return "8" + string((n-4)/2, '9') + "77" + string((n-4)/2, '9') + "8";
            if(n % 2 == 1) return "8" + string((n-3)/2, '9') + "8" + string((n-3)/2, '9') + "8";

        }else if(k == 7){
            if(n == 1) return "7";
            if(n == 2) return "77";
            if(n == 3) return "959";
            if(n == 4) return "9779";
            if(n == 5) return "99799";
            if(n == 7) return "9994999";
            if(n % 12 == 0) return string(n, '9');
            if(n % 12 == 1) return string((n-1)/2, '9') + "7" + string((n-1)/2, '9');
            if(n % 12 == 2) return string((n-2)/2, '9') + "77" + string((n-2)/2, '9');
            if(n % 12 == 3) return string((n-1)/2, '9') + "5" + string((n-1)/2, '9');
            if(n % 12 == 4) return string((n-2)/2, '9') + "77" + string((n-2)/2, '9');
            if(n % 12 == 5) return string((n-1)/2, '9') + "7" + string((n-1)/2, '9');
            if(n % 12 == 6) return string(n, '9');
            if(n % 12 == 7) return string((n-1)/2, '9') + "4" + string((n-1)/2, '9');
            if(n % 12 == 8) return string((n-2)/2, '9') + "44" + string((n-2)/2, '9');
            if(n % 12 == 9) return string((n-1)/2, '9') + "6" + string((n-1)/2, '9');
            if(n % 12 == 10) return string((n-2)/2, '9') + "44" + string((n-2)/2, '9');
            if(n % 12 == 11) return string((n-1)/2, '9') + "4" + string((n-1)/2, '9');

        }else if(k == 8){
            if(n == 1) return "8";
            if(n == 2) return "88";
            if(n == 3) return "888";
            if(n == 4) return "8888";
            if(n == 5) return "88888";
            return "888" + string(n-6, '9') + "888";
        }else if(k == 9){
            return string(n, '9');
        }

        return "";
    }
};
