class Solution {
public:
    string largestPalindrome(int n, int k) {


        if(k==1||k==3||k==9)
        {
            string s = "";
            for(int i = 1; i <= n; i++)
            {
                s += "9";
            }
            return s;
        }    
        else if(k==2)
        {
            if(n == 1) 
            {
                string s;
                s += "8";
                return s;
            }
            string s = "8";
            for(int i=1; i<=n-2;i ++)
            {
                s += "9";
            }
            s += "8";
            return s;
        }
        else if(k == 4)
        {
            if(n < 4)
            {
                string s = "";
                for(int i = 1; i <= n; i++)
                {
                    s += "8";
                }
                return s;
            }
            string s = "88";
            for(int i = 1; i <= n-4; i++)
            {
                s += "9";
            }
            s += "88";
            return s;
        }
        else if(k == 8)
        {
            if(n < 6)
            {
                string s = "";
                for(int i = 1; i <= n; i++)
                {
                    s += "8";
                }
                return s;
            }
            string s = "888";
            for(int i = 1; i <= n-6; i++)
            {
                s += "9";
            }
            s += "888";
            return s;
        }
        else if(k == 5)
        {
            if(n == 1)
            {
                string s = "5";
                return s;
            }
            string s = "5";
            for(int i = 1; i <= n-2; i++)
            {
                s += "9";
            }
            s +=  "5";
            return s;
        }
        else if(k==6)
        {
            if(n <= 2) 
            {
                string s;
                for(int i = 1; i <= n; i++) s += "6";
                return s;
            }
            if(n == 3)
            {
                string s = "888";
                return s;
            }
            string s = "8";
            for(int i=1; i<=n-2;i ++)
            {
                s += "9";
            }
            s += "8";

            if(n % 2 == 1)
            {
                s[n/2] = '8';
            }
            else
            {
                s[n/2-1] = '7';
                s[n/2] = '7';
            }
            return s;
        }
        else if(k == 7)
        {
            string strs[15] = {"999999","7","77","959","9779","99799","999999","9994999","99944999","999969999","9999449999","99999499999","999999999999"};
            if(n <= 12)
            {
                return strs[n];
            }
            string s = "";
            for(int i = 1; i <= n/12; i++)
            {
                s += "999999";
            }
            if(n % 12 != 0) s += strs[n%12];
            for(int i = 1; i <= n/12; i++)
            {
                s += "999999";
            }
            return s;
        }
        return "1";
    }
};
