bool isDivisibleBy7(const string& numberStr) {
    long long remainder = 0;
    for (char digit : numberStr) {
        if (digit < '0' || digit > '9') {
            cerr << "Invalid character in the string: " << digit << endl;
            return false;
        }
        remainder = (remainder * 10 + (digit - '0')) % 7;
    }
    return remainder == 0;
}

