        for (int i = 9; i >= 0; i--) {
            char x = '0' + i;
            s[n / 2] = x;
            if (isDivisibleBy7(s)) return s;
        }
    } else {
        for (int i = 9; i >= 0; i--) {
            char x = '0' + i;
            s[n / 2 - 1] = x;
            s[n / 2] = x;
            if (isDivisibleBy7(s)) return s;
        }
    }
    return s;
}

