class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(0)
        if k==1:
            return "9"*n
        if k==2:
            if n==1:
                return "8"
            return "8"+"9"*(n-2)+"8"
        if k==3:
            return "9"*n
        if k==4:
            if n==1:
                return "8"
            elif n==2:
                return "88"
            elif n==3:
                return "888"
            else:
                return "88"+"9"*(n-4)+"88"
        if k==5:
            if n==1:
                return "5"
            else:
                return "5"+"9"*(n-2)+"5"
        if k==6:
            if n==1:
                return "6"
            if n==2:
                return "66"
            if n%2==1:
                return "8"+"9"*((n-3)//2)+"8"+"9"*((n-3)//2)+"8"
            else:
                return "8"+"9"*((n-3)//2)+"77"+"9"*((n-4)//2)+"8"
        if k==7:
            if n==1:
                return "7"
            if n==2:
                return "77"
            if n%2==1:
                for i in range(9,-1,-1):
                    if (45+int("9"*((n-1)//2)+str(i)+"9"*(((n-1)//2)-1)))%7==0:
                        return "9"*((n-1)//2)+str(i)+"9"*((n-1)//2)
            else:
                for i in range(9,-1,-1):
                    if (45+int("9"*((n-2)//2)+str(i)*2+"9"*(((n-2)//2)-1)))%7==0:
                        return "9"*((n-2)//2)+str(i)*2+"9"*((n-2)//2)
        if k==8:
            if n<6:
                return "8"*n
            else:
                return "888"+"9"*(n-6)+"888"
        if k==9:
            return "9"*n
