class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        if k == 2:
            if n <= 2:
                return '8' * n
            else:
                return f"8{'9' * (n - 2)}8"
        if k == 4:
            if n <= 4:
                return '8' * n
            else:
                return f"88{'9' * (n - 4)}88"
        if k == 5:
            if n <= 2:
                return '5' * n
            else:
                return f"5{'9' * (n - 2)}5"
        if k == 8:
            if n <= 6:
                return '8' * n
            else:
                return f"888{'9' * (n - 6)}888"
        if k == 6:
            if n <= 2:
                return '6' * n
            elif n == 3:
                return "888"
            elif n == 4:
                return "8778"
            else:
                if n % 2 == 0:
                    n9 = int((n - 4) / 2)
                    return f"8{'9' * n9}77{'9' * n9}8"
                else:
                    n9 = int((n - 3) / 2)
                    return f"8{'9' * n9}8{'9' * n9}8"
        if k == 7:
            if n <= 2:
                return '7' * n
            else:
                ls = [5, 7, 7, 9, 4, 4, 6, 4, 4, 9, 7, 7]
                t=str(ls[(n-3)%12])
                if n % 2 == 0:
                    n9 = int((n - 2) / 2)
                    return f'{"9" * n9}{t}{t}{"9" * n9}'
                else:
                    n9 = int((n - 1) / 2)
                    return f'{"9" * n9}{t}{"9" * n9}'
