class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==9 or k==1 or k==3:return'9'*n
        if k==2:
            if n==1:return '8'
            return '8'+'9'*(n-2)+'8'
        if k==4:
            if n<=3:return '8'*n
            return '88'+'9'*(n-4)+'88'
        if k==5:
            if n==1:return'5'
            return '5'+'9'*(n-2)+'5'
        if k==6:
            if n==1:return '6'
            if n==2:return '66'
            if n&1:return '8'+'9'*((n-3)//2)+'8'+'9'*((n-3)//2)+'8'
            return '8'+'9'*((n-4)//2)+'77'+'9'*((n-4)//2)+'8'
        if k==8:
            return '8'*n if n<7 else '8'*3+'9'*(n-6)+'8'*3
        mul=pow(10,(n-1)//2,7)
        cur=int('9'*(n%6) if n%6 else 0)%7
        mid=9
        while cur%7:
            cur=cur-mul*(1 if n&1 else 11)
            cur%=7
            mid-=1
        if n&1:
            return '9'*((n-1)//2)+str(mid)+'9'*((n-1)//2)
        return '9'*((n-1)//2)+str(mid)*2+'9'*((n-1)//2)
