class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        if n <= 6:
            for x in range((10 ** n) - 1, (10 ** (n - 1)) - 1, -1):
                if x % k != 0:
                    continue
                s = str(x)
                if s == s[::-1]:
                    return s

        if k in (1, 3, 9):
            return '9' * n

        if k == 5:
            if n == 1:
                return '5'
            return '5' + (n - 2) * '9' + '5'

        if k == 2:
            if n == 1:
                return '8'
            return '8' + (n - 2) * '9' + '8'

        if k == 4:
            return '88' + (n - 4) * '9' + '88'

        if k == 8:
            return '888' + (n - 6) * '9' + '888'

        if k == 6:
            if n % 2 == 1:
                return '8' + (n - 3) // 2 * '9' + '8' + (n - 3) // 2 * '9' + '8'
            return '8' + (n - 4) // 2 * '9' + '77' + (n - 4) // 2 * '9' + '8'

        if k == 7:
            if n % 2 == 1:
                x = (n - 1) // 2
