class Solution {
public:

    bool check7(string s) {
        while(s.size() % 3 != 0) s = "0" + s;
        int n = s.size();
        int sum = 0;
        bool add = true;
        for(int i = n - 3; i >= 0; i -= 3) {
            if(add) sum += stoi(s.substr(i, 3));
            else sum -= stoi(s.substr(i, 3));
            add = !add;
        }
        return (sum % 7 == 0);
    }

    string largestPalindrome(int n, int k) {
        //9, 3, 1: 9999...
        //8: 888 999...999 888
        //2: 899..998
        //4: 88 999...999 88
        //5: 5 999...999 5
        //6: even digits: 897 999...999 798

        string res = "";
        if(k == 1 || k == 3 || k == 9) {
            for(int i = 0; i < n; ++i) res += "9";
            return res;
        }
        if(k == 8) {
            if(n < 6) {
                for(int i = 0; i < n; ++i) res += "8";
                return res;
            } else {
                res += "888";
                for(int i = 0; i < n - 6; ++i) res += "9";
                res += "888";
            }
            return res;
        }
        if(k == 2) {
            if(n < 2) {
                for(int i = 0; i < n; ++i) res += "8";
                return res;
            } else {
                res += "8";
                for(int i = 0; i < n - 2; ++i) res += "9";
                res += "8";
            }
            return res;
        }
        if(k == 4) {
            if(n < 4) {
                for(int i = 0; i < n; ++i) res += "8";
                return res;
            } else {
                res += "88";
                for(int i = 0; i < n - 4; ++i) res += "9";
                res += "88";
            }
            return res;
        }
        if(k == 5) {
            if(n < 2) {
                for(int i = 0; i < n; ++i) res += "5";
                return res;
            } else {
                res += "5";
                for(int i = 0; i < n - 2; ++i) res += "9";
                res += "5";
            }
            return res;
        }
        if(k == 6) {
            if(n == 1) return "6";
            if(n == 2) return "66";
            if(n % 2) {
                int sum = 16 + (n - 3) * 9;
                res += "8";
                for(int i = 0; i < (n - 3) / 2; ++i) res += "9";
                for(int i = 9; i >= 0; --i) {
                    if((sum + i) % 3 == 0) {
                        res += to_string(i);
                        break;
                    }
                }
                for(int i = 0; i < (n - 3) / 2; ++i) res += "9";
                res += "8";
            } else {
                int sum = 16 + (n - 4) * 9;
                res += "8";
                for(int i = 0; i < (n - 4) / 2; ++i) res += "9";
                for(int i = 9; i >= 0; --i) {
                    if((sum + i + i) % 3 == 0) {
                        res += to_string(i);
                        res += to_string(i);
                        break;
                    }
                }
                for(int i = 0; i < (n - 4) / 2; ++i) res += "9";
                res += "8";
            }
            return res;
        }
        if(k == 7) {
            for(int d = 9; d >= 0; --d) {
                string cur = "";
                for(int i = 0; i < (n - 1) / 2; ++i) cur += "9";
                cur += to_string(d);
                if(n % 2 == 0) cur += to_string(d);
                for(int i = 0; i < (n - 1) / 2; ++i) cur += "9";
                if(check7(cur)) return cur;
            }
            assert(false);
        }
        assert(false);
    }
};
