class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        if k == 1 or k == 3 or k == 9:
            return "9" * n

        if k == 2:
            if n == 1:
                return "8"
            elif n == 2:
                return "88"
            else:
                return "8" + "9" * (n - 2) + "8"

        if k == 5:
            if n == 1:
                return "5"
            elif n == 2:
                return "55"
            else:
                return "5" + "9" * (n - 2) + "5"

        if k == 6:
            if n == 1:
                return "6"
            if n == 2:
                return "66"
            elif n & 1 == 1:
                return "8" + "9" * ((n >> 1) - 1) + "8" + "9" * ((n >> 1) - 1) + "8" 
            else:
                return "8" + "9" * ((n >> 1) - 2) + "77" + "9" * ((n >> 1) - 2) + "8" 

        if k == 4:
            if n == 1:
                return "8"
            elif n == 2:
                return "88"
            elif n == 3:
                return "888"
            else:
                return "88" + "9" * (n - 4) + "88"

        if k == 8:
            if n == 1:
                return "8"
            elif n == 2:
                return "88"
            elif n == 3:
                return "888"
            elif n == 4:
                return "8888"
            elif n == 5:
                return "88888"
            else:
                return "888" + "9" * (n - 6) + "888"

        if k == 7:
            if n & 1 == 1:
                k = n >> 1
                m1 = pow(10, n, 7)
                m2 = pow(10, k, 7)

                for i in range(9):
                    if (m1 - 1 - m2 * i) % 7 == 0:
                        return "9" * k + f"{9-i}" + "9" * k

            else:
                k = n >> 1
                m1 = pow(10, n, 7)
                m2 = pow(10, k, 7) + pow(10, k - 1, 7)

                for i in range(9):
                    if (m1 - 1 - m2 * i) % 7 == 0:
                        return "9" * (k - 1) + f"{9-i}{9-i}" + "9" * (k - 1)

        return ""
