class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def check(s):
            x = 0
            for i in range(n):
                x = (x*10+s[i])%7
            return x == 0

        if k == 1:
            return '9'*n
        elif k == 2:
            s = ['9'] * n
            s[0] = s[-1] = '8'
            return ''.join(s)
        elif k == 3 or k == 9:
            return '9'*n
        elif k == 5:
            s = ['9'] * n
            s[0] = s[-1] = '5'
            return ''.join(s)
        elif k == 7:
            s = [9]*n
            if n&1:
                for i in range(9, 0, -1):
                    s[n//2] = i
                    if check(s):
                        return ''.join(map(str, s))
            else:
                for i in range(9, 0, -1):
                    s[n//2-1] = s[n//2] = i
                    if check(s):
                        return ''.join(map(str, s))
        elif k == 6:
            if n == 1:
                return '6'
            elif n == 2:
                return '66'
            elif n%2 == 0:
                s = ['9'] * n
                s[0] = s[-1] = '8'
                s[n//2-1] = s[n//2] = '7'
                return ''.join(s)
            else:
                s = ['9']*n
                s[0] = s[-1] = s[n//2] = '8'
                return ''.join(s)
        elif k == 4:
            if n == 1:
                return '8'
            else:
                s = ['9'] * n
                s[-2] = s[-1] = s[0] = s[1] = '8'
                return ''.join(s)
        elif k == 8:
            if n == 1:
                return '8'
            elif n == 2:
                return '88'
            else:
                s = ['9'] * n
                s[-3] = s[-2] = s[-1] = s[0] = s[1] = s[2] = '8'
                return ''.join(s)
