class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9: return "9" * n
        if k == 2: return "8" + "9" * (n-2) + "8" if n > 1 else "8"
        if k == 5: return "5" + "9" * (n-2) + "5" if n > 1 else "5"
        if k == 4: return ("88" + "9" * (n-4) + "88") if n > 3 else "8" * n
        if k == 8: return ("888" + "9" * (n-6) + "888") if n > 6 else "8" * n
        if k == 6:
            if n <= 2: return "6" * n
            if n % 2:
                s = "8" + "9" * (n//2-1)
                return s + "8" + s[::-1]
            else:
                s = "8" + "9" * (n//2-2)
                return s + "77" + s[::-1]
        if n < 3: return "7" * n
        m = n // 2
        p = n % 2
        m0 = m % 6
        m1 = m - m0
        if p:
            m = n // 2
            p = n % 2
            m0 = m % 6
            m1 = m - m0
            s = pow(10,(n-m0),7)
            t = pow(10,m,7)
            if m0 == 0:
                return "9" * m + "7" + "9" * m
            for i in range((10**(m0)) - 1, 6, -1):
                for j in range(9,0,-1):
                    if (i * s + int(str(i)[::-1]) + j * t) % 7 == 0:
                        return str(i) + "9"*m1 + str(j) + "9"*m1 + str(i)[::1]
        else:
            m = (n-2) // 2
            m0 = m % 6
            m1 = m - m0
            s = pow(10,(n-m0),7)
            t = pow(10,m,7)

            if m0 == 0:
                return "9" * m + "77" + "9" * m
            for i in range((10**(m0)) - 1, 6, -1):

                for j in range(9,0,-1):
                    if (i * s + int(str(i)[::-1]) + 11*j * t) % 7 == 0:
                        return str(i) + "9"*m1 + str(j)*2 + "9"*m1 + str(i)[::1]
