class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        elif k == 2:
            if n == 1:
                return '8'
            else:
                return f'8{"9" * (n - 2)}8'
        elif k == 4:
            if n <= 4:
                return '8' * n
            else:
                return f'88{"9" * (n - 4)}88'
        elif k == 5:
            if n == 1:
                return '5'
            else:
                return f'5{"9" * (n - 2)}5'
        elif k == 6:
            if n <= 2:
                return '6' * n
            elif n % 2 == 0:
                return f'8{"9" * ((n - 4) // 2)}77{"9" * ((n - 4) // 2)}8'
            else:
                return f'8{"9" * ((n - 3) // 2)}8{"9" * ((n - 3) // 2)}8'
        elif k == 7:
            a = 0
            b = 0
            for i in range(n):
                center = ((i == n // 2) or (i == n // 2 - 1)) if n % 2 == 0 else (i == n // 2)
                a = (a * 10 + (1 if not center else 0)) % 7
                b = (b * 10 + (1 if center else 0)) % 7
            a = (9 * a) % 7
            for i in range(9, -1, -1):
                if (i * b + a) % 7 == 0:
                    return "9" * ((n - 1) // 2) + f"{i}" * ((n - 1) % 2 + 1) + "9" * ((n - 1) // 2)
        elif k == 8:
            if n <= 6:
                return '8' * n
            else:
                return f'888{"9" * (n - 6)}888'
