class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        if k == 2:
            if n <= 2:
                return '8' * n
            return '8' + '9' * (n - 2) + '8'
        if k == 4:
            if n <= 4:
                return '8' * n
            return '88' + '9' * (n - 4) + '88'
        if k == 8:
            if n <= 6:
                return '8' * n
            else:
                return '888' + '9' * (n - 6) + '888'
        if k == 5:
            if n <= 2:
                return '5' * n
            return '5' + '9' * (n - 2) + '5'
        # 6 7
        if n <= 6:
            for i in range(10 ** n - 1, -1, -1):
                if i % k == 0 and str(i) == str(i)[::-1]:
                    return str(i)
        # if n <= 12 and n % 2 == 1:
        #     h = n // 2
        #     for i in range(10 ** h - 1, -1, -1):
        #         for j in range(9, -1, -1):
        #           s = str(i) + str(j) + str(i)[::-1]
        #           if int(s) % k == 0:
        #               return s
        # if n <= 12 and n % 2 == 0:
        #     h = n // 2
        #     for i in range(10 ** h - 1, -1, -1):
        #         s = str(i) + str(i)[::-1]
        #         if int(s) % k == 0:
        #             return s
        d = [1] * n
        for i in range(1, n):
            d[i] = d[i-1] * 10 % k
        h = (n - 2) // 2
        s = ''
        t = 0
        if k == 6:
            s = '8' + '9' * (h - 1)
        if k == 7:
            s = '9' * h
        for i in range(h):
            t += int(s[i]) * (d[i] + d[-i-1])
        t %= k
        r = n - h * 2
        for i in range(10 ** r - 1, -1, -1):
            t2 = i * d[h] % k
            if (t + t2) % k == 0 and str(i) == str(i)[::-1]:
                return s + str(i) + s[::-1]

        return ''
