class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s = "";
        int len = (n + 1) / 2, K = k, N = n;
        for (int i = 1; i <= len; i++)
            s += '9';
        if (k == 2 || k == 4)
            s[0] = '8';
        else if (k == 6) {
            if (n % 2 == 0) {
                s[0] = '8';
                s[len - 1] = '7';
            } else {
                s[0] = '8', s[len - 1] = '8';
            }
        } else if (k == 5)
            s[0] = '5';
        vector<int> f(n + 10, 0);
        int sum = 0, now = 1;
        if (N % 2 == 0) {
            for (int i = 0; i < len; i++) {
                f[i] = now; //权重
                sum = (sum + (s[i] - '0') * now) % K;
                now = now * 10 % K;
            }
            for (int i = len - 1; i >= 0; i--) {
                f[i] = (f[i] + now) % k;
                sum = (sum + (s[i] - '0') * now) % K;
                now = now * 10 % K;
            }
        } else {
            for (int i = 0; i < len; i++) {
                f[i] = now; //权重
                sum = (sum + (s[i] - '0') * now) % K;
                now = now * 10 % K;
            }
            for (int i = len - 2; i >= 0; i--) {
                f[i] = (f[i] + now) % k;
                sum = (sum + (s[i] - '0') * now) % K;
                now = now * 10 % K;
            }
        }
        while (1) {
            if (sum == 0) {
                if (n % 2 == 0) {
                    string ans = s;
                    reverse(s.begin(), s.end());
                    ans = ans + s;
                    return ans;
                } else {
                    string ans = s;
                    s.erase(len - 1);
                    reverse(s.begin(), s.end());
                    ans = ans + s;
                    return ans;
                }
            }
            int i = len - 1;
            while (s[i] == '0' || f[i] == 0)
                i--;
            s[i]--;
            sum = (sum - f[i] + k) % k;
            for (i++; i < len; i++) {
                s[i] = '9';
                sum = (sum + f[i] * 9) % k;
            }
        }
    }
};
