class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def is_divisible_by_7(num_str):
            current_mod = 0
            for digit in num_str:
                current_mod = (current_mod * 10 + int(digit)) % 7
            return current_mod == 0

        if k == 1:
            return '9' * n
        if k == 2:
            if n == 1:
                return '8'
            else:
                return '8' + '9' * (n - 2) + '8'
        if k == 3:
            return '9' * n
        if k == 4:
            if n == 1:
                return '8'
            elif n == 2:
                return '88'
            elif n == 3:
                return '888'
            else:
                return '88' + '9' * (n - 4) + '88'
        if k == 5:
            if n == 1:
                return '5'
            else:
                return '5' + '9' * (n - 2) + '5'
        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n == 3:
                return '888'
            if n % 2 == 0:
                return '8' + '9' * ((n - 4) // 2) + '77' + '9' * ((n - 4) // 2) + '8'
            else:
                return '8' + '9' * ((n - 3) // 2) + '8' + '9' * ((n - 3) // 2) + '8'
        if k == 7:
            if n == 1:
                return '7'
            if n == 2:
                return '77'
            half = '9' * ((n-1) // 2)
            for i in range(9, -1, -1):
                cur = half + (str(i) * (n - 2 * len(half))) + half
                if is_divisible_by_7(cur):
                    return cur

        if k == 8:
            if n == 1:
                return '8'
            if n == 2:
                return '88'
            if n == 3:
                return '888'
            if n == 4:
                return '8888'
            if n == 5:
                return '88888'
            return '888' + '9' * (n - 6) + '888'
        if k == 9:
            return '9' * n
