class Solution {
public:
    int mod;
    vector<int> p10s;
    string ans;
    vector<vector<int>> dp;
    int rec(int idx, int m, int n, int k) {
        if (idx == n) {
            if (m == 0) {
                return 1;
            }
            return 0;
        }
        if (dp[idx][m] != -1) return dp[idx][m];
        int d;
        if (n % 2 == 0) {
            d = n / 2 - 1;
        }
        else {
            d = n / 2;
        }
        if (idx > d) return rec(idx + 1, m, n, k);
        for (int i = 0; i <= 9; i++) {
            if (i == 0 && idx == 0) continue;
            int p10 = n - idx - 1;
            int tmp = p10s[p10];
            tmp = (tmp * i) % k;
            if (n - 1 - idx != idx) {
                int x = n - 1 - idx;
                int p102 = n - x - 1;
                int tmp2 = p10s[p10];
                tmp2 = (tmp2 * i) % k;
                tmp = (tmp + tmp2) % k;
            }
            int mm = (m + tmp) % k;
            if (rec(idx + 1, mm, n, k)) {
                return dp[idx][m] = 1;
            }
        }
        return dp[idx][m] = 0;
    }
    string largestPalindrome(int n, int k) {
        p10s.resize(n + 10);
        int aa = 1 % k;
        for (int i = 0; i < p10s.size(); i++) {
            p10s[i] = aa;
            aa = (aa * (10 % k)) % k;
        }
        ans.clear();
        ans = string(n, ' ');
        dp.assign(n, vector<int> (k, -1));
        mod = k;
        int m = 0;
        int d;
        if (n % 2) {
            d = n / 2;
        }
        else {
            d = n / 2 - 1;
        }
        for (int i = 0; i <= d; i++) {
            for (int j = 9; j >= 0; j--) {
                if (i == 0 && j == 0) continue;
                int p10 = n - i - 1;
                int tmp = p10s[p10];
                tmp = (tmp * j) % k;
                if (n - 1 - i != i) {
                    int x = n - 1 - i;
                    int p102 = n - x - 1;
                    int tmp2 = p10s[p102];
                    tmp2 = (tmp2 * j) % k;
                    tmp = (tmp + tmp2) % k;
                }
                int mm = (m + tmp) % k;
                if (rec(i + 1, mm, n, k)) {
                    m = mm;
                    ans[i] = '0' + j;
                    ans[n - 1 - i] = '0' + j;
                    break;
                }
            }
        }
        return ans;
    }
};
