class Solution {
public:
#define rep(n) for(int _i=0;_i<n;++_i)
    inline int qpow(int a, int b, int p) {
        int res = 1;
        for (; b; b >>= 1, a = 1ll * a * a % p)
            if (b & 1) res = 1ll * res * a % p;
        return res;
    }
    string largestPalindrome(int n, int k) {
        string s = "";

        if (k == 1 || k == 3 || k == 9) {
            rep(n) s += '9';
        }

        if (k == 5) {
            s += '5';
            for (int i = 1; i <= n - 2; ++i) s += '9';
            if (n > 1) s += '5';
        }
        if (k == 7) {
            if (n == 1) s = "7";
            else if (n == 2) s = "77";
            else {
                int r = 0, d = 9;
                rep(n) (r += d) %= 7, d = 10 * d % 7;

                int mul;
                if (n % 2) mul = qpow(10, n / 2, 7);
                else mul = qpow(10, n / 2 - 1, 7) * 11 % 7;

                r = (r - mul * 9 % 7 + 7) % 7;

                int x;
                for (int i = 9; i >= 0; --i)
                    if ((r + i * mul % 7) % 7 == 0) {
                        x = i;
                        break;
                    }

                int cnt = n % 2 ? n / 2 : n / 2 - 1;
                rep(cnt) s += '9';
                s += to_string(x);
                if (n % 2 == 0) s += to_string(x);
                rep(cnt) s += '9';
            }

        }

        if (k == 2) {
            s += '8';
            rep(n - 2) s += '9';
            if (n > 1) s += '8';
        }
        if (k == 4) {
            if (n <= 4) {
                rep(n) s += '8';
            }
            else {
                s += "88";
                rep(n - 4) s += '9';
                s += "88";
            }
        }
        if (k == 6) {
            if (n == 1) s = "6";
            else if (n == 2) s = "66";
            else if (n == 3) s = "888";
            else if (n == 4) s = "8778";
            else {
                int cnt = n % 2 ? (n - 3) / 2 : (n - 4) / 2;
                s += '8';
                rep(cnt) s += '9';
                s += (n % 2 ? "8" : "77");
                rep(cnt) s += '9';
                s += '8';
            }
        }
        if (k == 8) {
            if (n <= 6) {
                rep(n) s += '8';
            }
            else {
                s += "888";
                rep(n - 6) s += '9';
                s += "888";
            }
        }



        // int a = 7;
        // for (int d = 1; d <= 9; ++d) {
        //     int l = pow(10, d - 1), r = pow(10, d) - 1;
        //     for (int x = r; x >= l; --x) {
        //         if (x % a) continue;
        //         string s = to_string(x), t = s;
        //         reverse(t.begin(), t.end());
        //         if (s == t) {
        //             cout << x << '\n';
        //             break;
        //         }
        //     }
        // }

        return s;
    }
};
