class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        if k in (1,3,9):
            return '9' * n

        if k == 2:
            if n == 1:
                return '8'
            else:
                return '8' + '9' * (n - 2) + '8'

        if k == 4:
            if n <= 4:
                return '8' * n
            else:
                return '88' + '9' * (n - 4) + '88'

        if k == 8:
            if n <= 6:
                return '8' * n
            else:
                return '888' + '9' * (n - 6) + '888'


        if k == 5:
            if n == 1:
                return '5'
            else:
                return '5' + '9' * (n - 2) + '5'

        if k == 6:
            if n <= 2:
                return '6' * n
            if n == 3:
                return '888'
            if n == 4:
                return '8778'
            if n % 2 == 1:
                s = '8' + '9' * (n//2 - 1)
                return s + '8' + s[::-1]
            else:
                s = '8' + '9' * (n//2 - 2) + '7'
                return s + s[::-1]


        if k == 7:
            if n <= 2:
                return '7' * n

            rem = 0
            for i in range(n):
                rem = (rem * 10 + 9) % 7

            if rem == 0:
                return '9' * n

            if n % 2 == 1:
                mid = pow(10, n//2, 7)
            else:
                mid = (11 * pow(10, n//2 - 1, 7)) % 7

            cnt = 0
            while rem:
                rem = (rem - mid) % 7
                cnt += 1

            if n % 2 == 1:
                return '9' * (n//2) + str(9 - cnt) + '9' * (n//2)
            else:
                return '9' * (n//2 - 1) + str(9 - cnt) * 2 + '9' * (n//2 - 1)
