class Solution {
  public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9'); // NOLINT
        }
        if (k == 2) {
            if (n <= 2) {
                return string(n, '8'); // NOLINT
            }
            return "8" + string(n - 2, '9') + "8";
        }
        if (k == 4) {
            if (n <= 4) {
                return string(n, '8'); // NOLINT
            }
            return "88" + string(n - 4, '9') + "88";
        }
        if (k == 5) {
            if (n <= 2) {
                return string(n, '5'); // NOLINT
            }
            return "5" + string(n - 2, '9') + "5";
        }
        if (k == 6) {
            if (n < 3) {
                return string(n, '6'); // NOLINT
            }

            if (n % 2 == 1) {
                return "8" + string((n - 3) / 2, '9') + "8" +
                       string((n - 3) / 2, '9') + "8";
            }
            return "8" + string((n - 4) / 2, '9') + "77" +
                   string((n - 4) / 2, '9') + "8";
        }

        if (k == 7) { // what?
            auto mod = n % 12;
            if (mod <= 2) {
                return string((n - mod) / 2, '9') + string(mod, '7') +
                       string((n - mod) / 2, '9');
            }
            if (mod == 3) {
                return string((n - mod) / 2, '9') + "959" +
                       string((n - mod) / 2, '9');
            }
            if (mod == 4) {
                return string((n - mod) / 2, '9') + "9779" +
                       string((n - mod) / 2, '9');
            }
            if (mod == 5) {
                return string((n - mod) / 2, '9') + "99799" +
                       string((n - mod) / 2, '9');
            }
            if (mod == 6 || mod == 0) {
                return string(n, '9'); // NOLINT
            }

            if (mod == 7) {
                return string((n - mod) / 2, '9') + "9994999" +
                       string((n - mod) / 2, '9');
            }
            if (mod == 8 || mod == 10) {
                return string((n - 2) / 2, '9') + "44" +
                       string((n - 2) / 2, '9');
            }
            if (mod == 9) {
                return string((n - 3) / 2, '9') + "969" +
                       string((n - 3) / 2, '9');
            }
            if (mod == 11) {
                return string((n - 5) / 2, '9') + "99499" +
                       string((n - 5) / 2, '9');
            }
        }

        if (n <= 6) {
            return string(n, '8'); // NOLINT
        }
        return "888" + string(n - 6, '9') + "888";
    }
};
