def ispalin(n):
    s = str(n)
    return s==s[::-1]

class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n<=5:
            for i in range(pow(10,n)-1, -1,-1):
                if i%k==0 and ispalin(i): return str(i)
        if k==1 or k==3 or k==9:
            return '9'*n
        if k==2:
            return '8' + ('9'*(n-2)) + '8'
        if k==5:
            return '5' + ('9'*(n-2)) + '5'
        if k==4:
            return '88' + ('9'*(n-4)) + '88'
        if k==6:
            if n%2==1: 
                rp = '9' * ((n//2) - 1)
                return '8' + rp + '8' + rp + '8'
            else:
                rp = '9' *((n//2) - 2)
                return '8' + rp + '77' + rp + '8'
        if k==8:
            return '888' + ('9'*(n-6)) + '888'
        if k==7:
            v = 0
            for i in range(n):
                v = (10*v + 9)%7
            if v==0: return '9'*n
            if n%2==1:
                mul = pow(10, n//2, 7)
                for k in range(10):
                    if (k*mul)%7 == v:
                        return '9'*(n//2) + str(9-k) + '9'*(n//2)
            else:
                n9 = (n//2)-1
                mul = pow(10, n9, 7)
                for k in range(10):
                    if (k*11*mul)%7 == v:
                        return '9'*n9 + (str(9-k)*2) + '9'*n9
