class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def f(n):
            n2 = (n + 1) // 2
            for x in range(10**n2-1, 10**(n2-1)-1, -1):
                t = y = x
                if n % 2 == 1: t//= 10
                for _ in range(n - n2):
                    y = y * 10 + t % 10
                    t //= 10
                if y % k == 0: return str(y)
        if k in {1, 3, 9}: return '9' * n
        if k == 2:
            return '8' if n == 1 else '8' + '9' * (n-2) + '8'
        if k == 4:
            return f(n) if n < 4 else f'88{"9"*(n-4)}88'
        if k == 5:
            return '5' if n == 1 else f'5{"9"*(n-2)}5'
        if k == 6:
            if n < 4: return f(n)
            if n % 2 == 1: return f'8{"9"*((n-3)//2)}8{"9"*((n-3)//2)}8'
            return f'8{"9"*((n-4)//2)}77{"9"*((n-4)//2)}8'
        if k == 7:
            if n < 3: return f(n)
            # all 9 mod 7
            m9 = 0
            for _ in range(n):
                m9 = (m9 * 10 + 9) % 7
            # mid 1 or 11 mod 7
            m1 = (1 if n % 2 == 1 else 11) % 7
            n2 = (n-1)//2
            for _ in range(n2):
                m1 = (m1 * 10 + 0) % 7
            i = 0
            while (m9 - i * m1) % 7 != 0: i += 1
            # print((t:=int(f'{"9"*n2}{str(9-i)*(2-n%2)}{"9"*n2}')), t % 7)
            return f'{"9"*n2}{str(9-i)*(2-n%2)}{"9"*n2}'
        if k == 8:
            if n < 6: return f(n)
            return f'888{"9"*(n-6)}888'
