class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n == 1:
            return str(9//k*k)
        if k == 1 or k == 3 or k == 9:
            return "9"*n
        if k == 2:
            return "8"+"9"*(n-2)+"8"
        if k == 5:
            return "5"+"9"*(n-2)+"5"
        if k == 4:
            if n <= 4:
                return "8"*n
            return "88"+"9"*(n-4)+"88"
        if k == 8:
            if n <= 6:
                return "8"*n
            return "888"+"9"*(n-6)+"888"
        a = [9]*n
        if not k%2:
            a[0] = 8
            a[-1] = 8
        for i in range((n-1)//2, -1, -1):
            for _ in range(10):
                p = 0
                for j in a:
                    p *= 10
                    p += j
                    p %= k
                if p == 0:
                    return "".join(map(str, a))
                a[i] -= 1
                if i+i+1 != n:
                    a[-(i+1)] -= 1
