class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(n <= 5) {
            int ans = 0;
            for(int i = 0; i <= 99999; i ++ ) {
                string s = to_string(i);
                if(i % k == 0 && s.size() == n && s == string(s.rbegin(), s.rend())) {
                    ans = max(ans, i);
                }
            }
            return to_string(ans);
        }
        if(k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }
        if(k == 2) {
            return '8' + string(n - 2, '9') + '8';
        }
        if(k == 4) {
            return "88" + string(n - 4, '9') + "88";
        }
        if(k == 5) {
            return '5' + string(n - 2, '9') + '5';
        }
        if(k == 8) {
            return "888" + string(n - 6, '9') + "888";
        }
        if(k == 6) {
            string ans = '8' + string(n - 2, '9') + '8';
            if(n % 2) {
                ans[n / 2] -- ;
            } else {
                ans[n / 2] -= 2;
                ans[n / 2 - 1] -= 2;
            }
            return ans;
        }
        // k == 7
        string l = string((n - 1) / 2, '9');
        string ans;
        for(char m = '9'; m >= '0'; m -- ) {
            string s;
            if(n % 2) s = l + m + l;
            else s = l + m + m + l;
            int t = 0;
            for(char ch : s) {
                t = (t * 10 + ch - '0') % 7;
            }
            if(t == 0) {
                if(ans.size() == 0) {
                    ans = s;
                } else {
                    ans = max(ans, s);
                }
            }
        }
        return ans;
    }
};
