class Solution {
public:
    int check(string s){
        int n = s.length();
        int rem = 0;
        for (int i=0;i<n;i++){
            int val = rem*10 + (s[i]-'0');
            if (val>=7){
                rem = val%7;
            }
            else {
                if ((i+1)<n){
                    val *= 10;
                    val += (s[i+1]-'0');
                    rem = val%7;
                }
            }
        }
        if (rem==0)return 1;
        else return 0;
    }
    string largestPalindrome(int n, int k) {
        string ans;
        if (k==1 || k==3 || k==9){
            for (int i=0;i<n;i++)ans += '9';
            return ans;
        }
        if (k==2){
            if (n==1)return "8";
            ans += '8';
            for (int i=1;i<n-1;i++)ans += '9';
            ans += '8';
            return ans;
        }
        if (k==4){
            if (n==1)return "8";
            if (n==2)return "88";
            if (n==3)return "888";
            if (n==4)return "8888";
            ans += '8';
            ans += '8';
            for (int i=2;i<n-2;i++)ans += '9';
            ans += '8';
            ans += '8';
            return ans;
        }
        if (k==5){
            if (n==1)return "5";
            ans += '5';
            for (int i=1;i<n-1;i++)ans += '9';
            ans += '5';
            return ans;
        }
        if (k==6){
            if (n==1)return "6";
            if (n==2)return "66";
            ans += '8';
            int len = (n-3)/2;
            for (int i=0;i<len;i++)ans += '9';
            if (n%2==1)ans += '8';
            else {
                ans += '7';
                ans += '7';
            }
            for (int i=0;i<len;i++)ans += '9';
            ans += '8';
            return ans;
        }
        if (k==8){
            if (n<=6){
                for (int i=0;i<n;i++)ans += '8';
                return ans;
            }
            else {
                ans += '8';
                ans += '8';
                ans += '8';
                for (int i=1;i<=(n-6);i++){
                    ans += '9';
                }
                ans += '8';
                ans += '8';
                ans += '8';
                return ans;
            }
        }
        if (k==7){
            if (n==1)return "7";
            if (n==2)return "77";
            if (n==3)return "959";
            if (n==4)return "9779";
            if (n==5)return "99799";
            if (n==6)return "999999";
            if (n%2==1){
                int len = n/2;
                string s1,s2;
                for (int i=0;i<len;i++){
                    s1 += '9';
                    s2 += '9';
                }
                for (int i=9;i>=0;i--){
                    string s = s1;
                    s += i + '0';
                    s += s2;
                    if (check(s))return s;
                }
            }
            else {
                int len = (n-1)/2;
                string s1,s2;
                for (int i=0;i<len;i++){
                    s1 += '9';
                    s2 += '9';
                }
                for (int i=9;i>=0;i--){
                    string s = s1;
                    s += i + '0';
                    s += i + '0';
                    s += s2;
                    if (check(s))return s;
                }
            }
        }
        return "9";
    }
};
