/**
 * @param {number} n
 * @param {number} k
 * @return {string}
 */
var largestPalindrome = function(n, k) {
    const a = new Array(n).fill(9);
    if (k === 1 || k === 3 || k === 9) {
        return a.join('');
    }
    if (k === 2) {
        a[0] = 8;
        a[n - 1] = 8;
        return a.join('');
    }
    if (k === 4) {
        a[0] = 8;
        a[n - 1] = 8;
        if (n > 2) {
            a[1] = 8;
            a[n - 2] = 8;
        }
        return a.join('');
    }
    if (k === 5) {
        a[0] = 5;
        a[n - 1] = 5;
        return a.join('');
    }
    if (k === 6) {
        if (n === 1) return '6';
        if (n === 2) return '66';
        a[0] = 8;
        a[n - 1] = 8;
        if (n % 2 === 1) {
            a[Math.floor(n / 2)] = 8;
        } else {
            a[n / 2] = 7;
            a[n / 2 - 1] = 7;
        }
        return a.join('');
    }
    if (k === 8) {
        for (let i = 0; i < 3; i++) {
            if (i >= n) break;
            a[i] = 8;
            a[n - 1 - i] = 8;
        }
        return a.join('');
    }
    const t = n % 12;
    const f = ['', '7', '77', '959', '9779', '99799', '999999', '9994999', '99944999','999969999', '9999449999', '99999499999'];
    const l = Math.floor(n / 12);
    const b = new Array(l * 6).fill('9').join('');
    return b + f[t] + b;
};
