class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) return string(n, '9');
        if (k == 2) return n == 1 ? "8" : "8" + string(n - 2, '9') + "8";
        if (k == 5) return n == 1 ? "5" : "5" + string(n - 2, '9') + "5";
        if (k == 4) return n <= 3 ? string(n, '8') : "88" + string(n - 4, '9') + "88";
        if (k == 8) return n <= 5 ? string(n, '8') : "888" + string(n - 6, '9') + "888";

        if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";
            if (n % 2 == 1)  return "8" + string((n - 3) / 2, '9') + "8" + string((n - 3) / 2, '9') + "8";
            return "8" + string(n / 2 - 2, '9') + "77" + string(n / 2 - 2, '9') + "8";
        }

        if (k == 7) {
            if (n <= 2) return string(n, '7');

            string ans(n, '9');
            if (n % 2 == 1) {
                for (int i = 9; i >= 0; i--) {
                    ans[n / 2] = i + '0';

                    int r = 0;
                    for (int j = 0; j < n; j++) {
                        r = (r * 10 + ans[j] - '0') % 7;
                    }
                    if (r == 0) break;
                }
            }
            else {
                for (int i = 9; i >= 0; i--) {
                    ans[n / 2] = i + '0';
                    ans[n / 2 - 1] = i + '0';

                    int r = 0;
                    for (int j = 0; j < n; j++) {
                        r = (r * 10 + ans[j] - '0') % 7;
                    }
                    if (r == 0) break;
                }
            }
            return ans;
        }

        return string(n, '9');
    }
};
