class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        if k == 2:
            if n == 1:
                return "8"
            else:
                return "8" + "9" * (n - 2) + "8"
        if k == 5:
            if n == 1:
                return "5"
            else:
                return "5" + "9" * (n - 2) + "5"
        if k == 4:
            if n == 1:
                return "8"
            elif n == 2:
                return "88"
            elif n == 3:
                return "888"
            else:
                return "88" + "9" * (n - 4) + "88"
        if k == 8:
            if n == 1:
                return "8"
            elif n == 2:
                return "88"
            elif n == 3:
                return "888"
            elif n == 4:
                return "8888"
            elif n == 5:
                return "88888"
            else:
                return "888" + "9" * (n - 6) + "888"
        if k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            elif n % 2 == 0:
                m = (n - 4) // 2
                return "8" + "9" * m + "77" + "9" * m + "8"
            else:
                m = (n - 3) // 2
                return "8" + "9" * m + "8" + "9" * m + "8"
        if k == 7:
            if n == 1:
                return "7"
            elif n == 2:
                return "77"
            elif n % 2 == 0:
                m = (n - 2) // 2
                s = (pow(10, m, 7) + 6) % 7
                s = (s + s * pow(10, m + 2, 7)) % 7
                for i in reversed(range(10)):
                    if (s + i * 11 * pow(10, m, 7)) % 7 == 0:
                        return "9" * m + str(i) * 2 + "9" * m
            else:
                m = (n - 1) // 2
                s = (pow(10, m, 7) + 6) % 7
                # print(s)
                s = (s + s * pow(10, m + 1, 7)) % 7
                # print(s)
                for i in reversed(range(10)):
                    if (s + i * pow(10, m, 7)) % 7 == 0:
                        return "9" * m + str(i) * 1 + "9" * m
