class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans = "" ;
        if(k==1 || k==3 || k==9){
            for(int i=0;i<n;i++){
                ans += "9" ;
            }
        }
        else if(k==2){
            ans+="8" ;
            for(int i=1;i<n-1;i++){
                ans+="9" ;
            }
            if(n>1){
                ans+="8" ;
            }
        }
        else if(k==4){
            if(n==1) return "8" ;
            if(n==2) return "88" ;
            if(n==3) return "888" ;
            if(n==4) return "8888" ;
            ans += "88" ;
            for(int i=2;i<n-2;i++){
                ans+="9" ;
            }
            ans+="88" ;
        }
        else if(k==5){
            ans+="5" ;
            for(int i=1;i<n-1;i++) ans += "9" ;
            if(n>1) ans+="5" ;
        }
        else if(k==6){
            if(n==1) return "6" ;
            if(n==2) return "66" ;
            ans+="8" ;
            for(int i=1;i<n-1;i++){
                ans+="9" ;
            }
            ans+="8" ;
            if(n%2==0){
                ans[n/2] = '7' ;
                ans[n/2 - 1] = '7' ;
            }
            else{
                ans[n/2] = '8' ;
            }
        }
        else if(k==7){
            if(n==1) return "7" ;
            if(n==2) return "77" ;
            int p = n/6 ;
            p = p%2 ;
            char c1 = '4';
            char c2 = '6' ;
            if(p==0){
                c1 = '7' ;
                c2 = '5' ;
            }
            int x = n%6 ;

            for(int i=0;i<n;i++) ans+="9" ;
            if(x==0){

            }
            else if(x==1 || x==5){
                ans[n/2] = c1 ;
            }
            else if(x==2 || x==4){
                ans[n/2] = c1 ;
                ans[n/2 - 1] = c1 ;
            }
            else{
                ans[n/2] = c2 ;   
            }
        }
        else{
            if(n==1) return "8" ;
            if(n==2) return "88" ;
            if(n==3) return "888" ;
            if(n==4) return "8888" ;
            if(n==5) return "88888" ;
            if(n==6) return "888888" ;
            ans += "888" ;
            for(int i=3;i<n-3;i++){
                ans+="9" ;
            }
            ans+="888" ;
        }
        return ans ;
    }
};
