class Solution {
public:


    bool isPalindrome(int x) {
        string s = std::to_string(x);
        for (int i = 0, j = s.size() - 1; i < j; i++, j--) {
            if (s[i] != s[j]) return false;
        }
        return true;
    }
    string construct(string x, int k) {
        string t = "";
        for (int i = 0; i < k; i++) t += x;
        return t;
    }
    int getmod(string s, int k) {
        int currMod = 0;

        for (int i = 0; i < s.size(); i++) {
            currMod *= 10;
            currMod += s[i] - '0';
            currMod %= k;
        }
        return currMod;
    }
    string largestPalindrome(int n, int k) {
        if (n <= 7) {
            int max = 1; 
            for (int i = 1; i <= n; i++) {
                max *= 10;
            }
            for (int i = max - 1; i >= 1; i--) {
                if ((i % k) == 0 && isPalindrome(i)) {
                    return std::to_string(i);
                }
            }
        }
        if (k == 2) {
            return "8" + construct("9", n - 2) + "8";
        }
        if (k == 3 || k == 9) {
            return construct("9", n);
        }
        if (k == 4) {
            return "88" + construct("9", n - 4) + "88";
        }
        if (k == 8) {
            return "888" + construct("9", n - 6) + "888";
        }
        if (k == 5) {
            return "5" + construct("9", n - 2) + "5";
        }
        if (k == 6) {
            if (n % 2 == 1) {
                return "8" + construct("9", n / 2 - 1) + "8" + construct("9", n / 2 - 1) + "8";
            } else {
                return "8" + construct("9", n / 2 - 2) + "77" + construct("9", n / 2 - 2) + "8"; 
            }
            return "";
        }
        // k == 7
        if (n % 2 == 0) {
            for (int digit = '9'; digit >= '0'; digit--) {
                string s = construct("9", n/2 - 1);
                s += digit;
                s += digit;
                s += construct("9", n/2 - 1);
                if (getmod(s, k) == 0) {
                    return s;
                }
            }
        }  
        if (n % 2 == 1) {
            for (int digit = '9'; digit >= '0'; digit--) {
                string s = construct("9", n/2);
                s += digit;
                s += construct("9", n/2);
                if (getmod(s, k) == 0) {
                    return s;
                }
            }
        }   
        return "0";
    }
};
