class Solution {
    int a[100005];
    char s[100005];
    bool check(int n, int m,int k){
        int now = 0;
        for(int i=0;i<m;i++){
            now = (now*10+a[i])%k;
        }
        for(int i=m-1-(n%2); i>=0;i--){
            now = (now*10+a[i])%k;
        }
        return now==0;
    }
    void dec(int m){
        a[m-1]--;
        for(int i=m-1;i>0;i--){
            if(a[i]<0){
                a[i]+=10;
                a[i-1]--;
            }
        }
    }
public:
    string largestPalindrome(int n, int k) {
        int m = (n+1)/2;
        for(int i=0;i<m;i++) a[i] = 9;
        if(k%2==0){
            a[0] = 8;
        }
        if(k%4==0){
            // 88
            a[0] = 8;
            a[1] = 8;
        }
        if(k%8==0 ){
            // 875
            a[0] = 8;
            a[1] = 8;
            a[2] = 8;
        }
        if(k%5==0){
            a[0] = 5;
        }
        while(!check(n,m,k)){
            dec(m);
        }
        for(int i=0;i<m;i++){
            s[i]=a[i]+'0';
        }
        for(int i=m-1;i>=0;i--){
            s[n-i-1] = a[i]+'0';
        }
        return string(s, s+n);
    }
};
