class Solution {
public:
    int g7mod(string& s) {
        int val = 0;
        for (int i = s.size() - 1; i >= 0; i--) {
            val = (10 * val + s[i] - '0') % 7;
        }

        return val;
    }

    string largestPalindrome(int n, int k) {
        string r = "";
        for (int i = 0; i < n; i++) r += '9';

        // 4 6 7 8
        if (k == 1 || k == 3 || k == 9) {
            return r;
        } else if (k == 2) {
            r[0] = '8';
            r[n - 1] = '8';
            return r;
        } else if (k == 5) {
            r[0] = '5';
            r[n - 1] = '5';
            return r;
        } else if (k == 4) {
            if (n == 1) return "8";
            r[0] = '8';
            r[1] = '8';
            r[n - 1] = '8';
            r[n - 2] = '8';
            return r;
        } else if (k == 8) {
            if (n == 1) return "8";
            if (n == 2) return "88";
            r[0] = '8';
            r[1] = '8';
            r[2] = '8';
            r[n - 1] = '8';
            r[n - 2] = '8';
            r[n - 3] = '8';
            return r;
        } else if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";

            r[0] = '8';
            r[n - 1] = '8';

            if (n % 2 == 0) {
                r[n / 2] = '7';
                r[n / 2 - 1] = '7';
            } else {
                r[n / 2] = '8';
            }

            return r;
        } else {
            for (int i = 9; i >= 0; i--) {
                char c = i + '0';
                if (n % 2 == 0) {
                    r[n / 2] = c;
                    r[n / 2 - 1] = c;
                } else {
                    r[n / 2] = c;
                }

                if (g7mod(r) == 0)
                    return r;
            }
        }

        return r;
    }
};
